import React from 'react';
import { Grid, makeStyles, Typography } from '@material-ui/core'
import ProfilePic from '../Images/ProfilePic.jpg';


const useStyles = makeStyles((theme) => ({
    background: {
        width: '100vw',
        margin: '0px'
    },
    image: {
        width: '80%',
    },
    text: {
        color: "#f1f1f1",
        backgroundColor: 'rgba(255, 255, 255, 0.1)',
        padding: "0px"
    }
}));

function Home() {
    const classes = useStyles();

    return (
        <Grid container justify="center" alignItems="center" className={classes.background} spacing={3}>

            {/* Inhoud tekst */}
            <Grid item xs={9} sm={6} className={classes.text} >
                <Typography variant="h4" gutterBottom>
                    Hi, I'm Furkan Öztürk
                 </Typography>
                <Typography variant="body1" >
                    I'm a front-end developer and UI/UX designer.
                 </Typography>
            </Grid>

            {/* Profiel foto */}
            <Grid item xs={9} sm={6} >
                <img className={classes.image} src={ProfilePic} alt="profilePhoto" />
            </Grid>

        </Grid>
    );
}

export default Home;
