import React from 'react';
import { Grid, makeStyles } from '@material-ui/core'
import { HashRouter, NavLink, Switch, Route } from 'react-router-dom';
import './Styling/Menu.css';
import Home from './Home';
import Work from './Work.js';
import About from './About';
import cursorDot from 'cursor-dot';

const useStyles = makeStyles((theme) => ({
    root: {
        backgroundColor: 'rgba(255, 255, 255, 0.1)',
    },
    menuItem: {
        padding: "10px",
        paddingBottom: "25px",
        fontSize: '60%',
    },
    menu: {
        backgroundColor: 'rgba(37, 37, 37, 1)',
    },
    content: {
        maxWidth: 1920,
        height: "100%",
    }

}));

const cursor = cursorDot({
    // diameter in pixels
    diameter: 50,

    // border width
    borderWidth: 1,

    // border color
    borderColor: '#fff',

    // easing
    easing: 4,

    // background
    background: 'transparent'

})

function Main() {
    const classes = useStyles();
    return (
        <Grid container direction="column" alignItems="flex-start" cursor className={classes.root}>

            {/* Menu routing */}
            <Grid item container direction="row" justify="center" >
                <HashRouter>
                    <Grid item container
                        justify="center"
                        xs={12}
                        className={classes.menu}
                    >
                        <Grid item className={classes.menuItem} align="center" xs={4} sm={2}>
                            <NavLink exact to="/">Home</NavLink>
                        </Grid>
                        <Grid item className={classes.menuItem} align="center" xs={4} sm={2}>
                            <NavLink to="/Work">Work</NavLink>
                        </Grid>
                        <Grid item className={classes.menuItem} align="center" xs={4} sm={2}>
                            <NavLink to="/About">About</NavLink>
                        </Grid>
                    </Grid>

                    {/* De content */}
                    <Grid item container className={classes.content}>
                        <Switch>
                            <Route exact path="/" component={Home} />
                            <Route path="/Work" component={Work} />
                            <Route path="/About" component={About} />
                        </Switch>
                    </Grid>

                </HashRouter>
            </Grid>
        </Grid>

    )
}

export default Main;
