import React from 'react';
import { Grid, makeStyles, Typography } from '@material-ui/core'
import ProfilePic from '../Images/ProfilePic.jpg';


const useStyles = makeStyles((theme) => ({
    background: {
        width: '100vw',
        margin: '0px'
    },
    image: {
        width: '80%',
    }
}));

function Home() {
    const classes = useStyles();

    return (
        <Grid container justify="center" className={classes.background} spacing={2}>

            {/* Profiel foto */}
            <Grid item container xs={12} md={5} alignItems="flex-end">
                <img className={classes.image} src={ProfilePic} alt="profilePhoto" />
            </Grid>

            {/* Inhoud tekst */}
            <Grid item container align="center" xs={12} md={5} alignItems="flex-start">
                <Typography variant="h4" gutterBottom>
                    Hi, I'm Furkan Öztürk
                 </Typography>
                <Typography variant="body1" gutterBottom>
                    Hi, I'm Furkan Öztürk
                 </Typography>
            </Grid>

        </Grid>
    );
}

export default Home;
