import React from 'react';
import './Styling/ProgressBar.css';

const Progress = ({ done }) => {
    const [style, setStyle] = React.useState({});

    setTimeout(() => {
        const newStyle = {
            opacity: 1,
            width: `${done}%`
        }

        setStyle(newStyle);
    }, 200);

    return (
        <div className="progress">
            <div className="progress-done" style={style}>
                <Progress done="70%" />
            </div>
        </div>
    )
}

export default Progress;
