import React from 'react';
import { Grid, makeStyles, Typography } from '@material-ui/core'
import ProfilePic from '../Images/ProfilePic.jpg';


const useStyles = makeStyles((theme) => ({
    background: {
        width: '100vw',
        height: '93vh',
        margin: '0px'
    },
    image: {
        maxHeight: '100%',
        maxWidth: "100%",
        borderRadius: 4,
        padding: 0,
    },
    text: {
        color: "#f1f1f1",
        // backgroundColor: 'rgba(255, 255, 255, 0.1)',
        padding: "0px",
        width: "100%",
    },
    textItem: {
        padding: 10
    },
    imageItem: {
        height: '100%',
        width: '100%',

        // backgroundColor: 'rgba(255, 255, 255, 0.1)',
    }
}));

function Home() {
    const classes = useStyles();

    return (
        <Grid container justify="center" alignItems="center" className={classes.background} >

            {/* Inhoud tekst */}
            <Grid item container justify="center" alignItems="center" xs={12} md={6} className={classes.text} >
                <Grid item container alignContent="center" justify="center" className={classes.textItem}>
                    <Grid item>
                        <Typography variant="h4" gutterBottom>
                            Hi, I'm Furkan Öztürk
                        </Typography>
                        <Typography variant="body1" >
                            I'm a front-end developer and UI/UX designer.
                        </Typography>
                    </Grid>
                </Grid>
            </Grid>

            {/* Profiel foto */}
            <Grid container item xs={8} md={6} className={classes.imageItem} alignItems="center" justify="flex-end">
                <img className={classes.image} align="right" src={ProfilePic} alt="profilePhoto" />
            </Grid>

        </Grid>
    );
}

export default Home;
