import React, { useState } from 'react';
import { Grid, makeStyles } from '@material-ui/core'
import { HashRouter, NavLink, Switch, Route } from 'react-router-dom';
import './Styling/Menu.css';
import Home from './Home';
import Work from './Work.js';
import About from './About';
import cursorDot from 'cursor-dot';
import { useSpring, animated } from 'react-spring'

const useStyles = makeStyles((theme) => ({
    root: {
        backgroundColor: 'rgba(255, 255, 255, 0.1)',
    },
    menuItem: {
        padding: "10px",
        paddingBottom: "25px",
        fontSize: '60%',
    },
    menu: {
        backgroundColor: 'rgba(37, 37, 37, 1)',
    },
    content: {
        maxWidth: 1920,
        height: "100%",
    }

}));

const cursor = cursorDot({
    // diameter in pixels
    diameter: 50,

    // border width
    borderWidth: 1,

    // border color
    borderColor: '#fff',

    // easing
    easing: 4,

    // background
    background: 'transparent'

})

function Main() {
    const classes = useStyles();
    const [state, toggle] = useState(true)
    const { x } = useSpring({ from: { x: 0 }, x: state ? 1 : 0, config: { duration: 1000 } })
    return (
        <Grid container direction="column" alignItems="flex-start" cursor className={classes.root}>

            {/* Menu routing */}
            <Grid item container direction="row" justify="center" >
                <HashRouter>
                    <Grid item container
                        justify="center"
                        xs={12}
                        className={classes.menu}
                    >
                        <Grid item className={classes.menuItem} align="center" xs={4} sm={2}>
                            <NavLink exact to="/" onClick={() => toggle(!state)}>Home</NavLink>
                        </Grid>
                        <Grid item className={classes.menuItem} align="center" xs={4} sm={2}>
                            <NavLink to="/Work" onClick={() => toggle(!state)}>Work</NavLink>
                        </Grid>
                        <Grid item className={classes.menuItem} align="center" xs={4} sm={2}>
                            <NavLink to="/About" onClick={() => toggle(!state)}>
                                <animated.div
                                    style={{
                                        opacity: x.interpolate({ range: [0, 1], output: [0.3, 1] }),
                                        transform: x
                                            .interpolate({
                                                range: [0, 0.25, 0.35, 0.45, 0.55, 0.65, 0.75, 1],
                                                output: [1, 0.97, 0.9, 1.1, 0.9, 1.1, 1.03, 1]
                                            })
                                            .interpolate(x => `scale(${x})`)
                                    }}>
                                    About
      </animated.div>
                            </NavLink>
                        </Grid>
                    </Grid>

                    {/* De content */}
                    <Grid item container className={classes.content}>
                        <Switch>
                            <Route exact path="/" component={Home} />
                            <Route path="/Work" component={Work} />
                            <Route path="/About" component={About} />
                        </Switch>
                    </Grid>

                </HashRouter>
            </Grid>
        </Grid>

    )
}

export default Main;
